import Axios from 'axios';
import { GET_CUSTOMERS, IS_LOGIN } from './actionType';
import { URL } from "../common/API";
import { useCookies } from 'react-cookie';

const header =   { headers: { 'Content-Type' : 'application/json', 'Accept': 'application/json'  } };

export const getCustomer = () => {
    return dispatch => {
        Axios.get(URL.customerLists)
        .then(res => {
            dispatch({
              type: GET_CUSTOMERS,
              payload: res.data
            });
          })
          .catch(err => {
              dispatch({
                type: GET_CUSTOMERS,
                payload: {"error":"Error Connection"}
              });
          });
    }
}

export const login = (e) => {
  const [cookies, setCookie] = useCookies();
  return dispatch => {
      Axios.post(URL.login, e, header )
      .then(res => {
        console.log( cookies.get('accessToken'));
          dispatch({
            type: IS_LOGIN,
            payload: res.data
          });
        })
        .catch(err => {
            dispatch({
              type: IS_LOGIN,
              payload: {"error":"Error Connection"}
            });
        });
  }
}
