import Axios from 'axios';
import { GET_PRODUCTS, IS_LOGIN } from './actionType';
import { URL } from "../common/API";

export const getProducts = () => {
    return dispatch => {
        Axios.get(URL.Login)
        .then(res => {
            dispatch({
              type: GET_PRODUCTS,
              payload: res.data
            });
          })
          .catch(err => {
              dispatch({
                type: GET_PRODUCTS,
                payload: {"error":"Error Connection"}
              });
          });
    }
}

export const login = (e) => {
  return dispatch => {
      Axios.post(URL.Login, e)
      .then(res => {
        console.log(res)
          dispatch({
            type: IS_LOGIN,
            payload: res.data
          });
        })
        .catch(err => {
            dispatch({
              type: GET_PRODUCTS,
              payload: {"error":"Error Connection"}
            });
        });
  }
}
