import React  from "react";
import { Component } from "react";
import * as Yup from "yup";
import { Redirect } from "react-router-dom";
import { Button, Container, Row, Col } from "react-bootstrap";
import { Formik, Field, Form, ErrorMessage } from "formik";
import { login } from '../action/app.action';
import { bindActionCreators } from "redux";
import { connect } from "react-redux";

 class LoginComponent extends Component  {
    constructor(props) {
        super(props);
        this.state = {
          redirect: false,
          error: ""
        };
        // this.props.logOut();
      }
      static getDerivedStateFromProps(nextProps, prevState) {
        if(nextProps.tokeProps && nextProps.tokeProps['error'] !== prevState.error){
          return{
            error:nextProps.tokeProps['error']
          }
        }
        return null
      }
     
      componentDidUpdate(prevProps, prevState){
        if(prevProps.tokeProps !== this.props.tokeProps){
          this.renderRedirect()
        }
      }
    
      renderRedirect = () => {
        let tokeProps = this.props.tokeProps;
        if (tokeProps && tokeProps.token) {
          localStorage.setItem("AccessToken", tokeProps.token);
        }
        let storedToken = localStorage.getItem("AccessToken");
        if (storedToken && storedToken != "undefined") {
         return <Redirect to="/dashboard" />
        } else {
          return <Redirect to="/login" />;
        }
      };
      login = fields => {
        this.props.login(fields);
      };
      render() {
        return (
          <Container>
            {this.renderRedirect()}
            <Row className="justify-content-md-center rows">
                <div id="formContent">
            <Col xs="12"  lg="12">
                <h2 className="text-center">
                    Login
                </h2>
                <hr/>
                </Col>
              <Col xs="12"  lg="12">
                <Formik
                  className="loginForm"
                  initialValues={{
                    username: "",
                    password: ""
                  }}
                  validationSchema={Yup.object().shape({
                    username: Yup.string()
                      .required("usernameis required")
                      .required("username is required"),
                    password: Yup.string()
                      .min(6, "Password must be at least 6 characters")
                      .required("Password is required")
                  })}
                  onSubmit={fields => {
                    console.log(fields);
                    this.login(fields);
                  }}
                  render={({ errors, status, touched }) => (
                    <Form >
                      <div className="form-group">
                        <label htmlFor="username"> Username </label>{" "}
                        <Field
                          name="username"
                          type="text"
                          className={
                            "form-control" +
                            (errors.username && touched.username
                              ? " is-invalid"
                              : "")
                          }
                        />{" "}
                        <ErrorMessage
                          name="username"
                          component="div"
                          className="invalid-feedback"
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="password"> Password </label>{" "}
                        <Field
                          name="password"
                          type="password"
                          className={
                            "form-control" +
                            (errors.password && touched.password
                              ? " is-invalid"
                              : "")
                          }
                        />{" "}
                        <ErrorMessage
                          name="password"
                          component="div"
                          className="invalid-feedback"
                        />
                      </div>
                      <div className="form-group">
                        <Button type="submit"  variant="primary" size="lg" block>
                          {" "}
                          Login{" "}
                        </Button>{" "}
                        <p className="error"> {this.state.error? this.state.error:null} </p>
                      </div>
                    </Form>
                  )}
                />
              </Col>{" "}
              </div>
            </Row>{" "}
          </Container>
        );
      }
}

const mapStateToProps = state => {
  return{
  product: state.appReducer
};
};


const mapDispatchToState = dispatch => {
  return bindActionCreators({
      login: login
  }, dispatch)
}

export default connect(mapStateToProps, mapDispatchToState)(LoginComponent);
