import Axios from 'axios';
import { GET_CUSTOMERS, IS_LOGIN } from './actionType';
import { URL } from "../common/API";

const header =   { headers: { 'Content-Type' : 'application/javascript'} };

export const getCustomer = () => {
    return dispatch => {
        Axios.get(URL.customers)
        .then(res => {
            dispatch({
              type: GET_PRODUCTS,
              payload: res.data
            });
          })
          .catch(err => {
              dispatch({
                type: GET_PRODUCTS,
                payload: {"error":"Error Connection"}
              });
          });
    }
}

export const login = (e) => {
  return dispatch => {
      Axios.post(URL.Login, e, header)
      .then(res => {
          dispatch({
            type: IS_LOGIN,
            payload: res.data
          });
        })
        .catch(err => {
            dispatch({
              type: IS_LOGIN,
              payload: {"error":"Error Connection"}
            });
        });
  }
}
