import React from "react";
import { BrowserRouter as Router, Redirect, Route } from 'react-router-dom';

// Routing components
import {Layout} from './ui_modules/Layout';
import {Headers} from "./ui_modules/Header";
import {SideBarComponent} from "./ui_modules/Sidebar";

// Pages
import LoginComponent from './pages/login';
import HomeComponent from "./pages/Home";
import AddCustomerComponent from "./pages/Addcustomer";
import CustomerListComponent from "./pages/CustomerList";



const pathList = [
  {
    path:'/',
    component: HomeComponent
  },
  {
    path:'/dashboard',
    component: HomeComponent
  },
  {
    path: '/dashboard/add-customer',
    component: AddCustomerComponent
  },
  {
    path: '/dashboard/customer-list',
    component: CustomerListComponent
  }
]






export const AppRoute = () => {
  let token = localStorage.getItem('accessToken');
  return (
    <Router>
       {token && <SideBarComponent></SideBarComponent> }           
           {token && <Headers></Headers> }
      { token ?   pathList.map( (compList, id) =>  <Layout key={id} exact path={compList.path}  component={compList.name} /> ) : <Redirect to={{ pathname: '/login'}}   /> }
      <Route path="/login" exact component={LoginComponent} /> 
    </Router>
  );
};
