//             });
//           })
//           .catch(err => {
//               dispatch({
//                 type: GET_PRODUCTS,
//                 payload: {"error":"Error Connection"}
//               });
//           });
//     }
// }

export const login = (e) => {
  return dispatch => {
      Axios.post(URL.Login, e, header)
      .then(res => {
          dispatch({
            type: IS_LOGIN,
            payload: res.data
          });
        })
        .catch(err => {
            dispatch({
              type: IS_LOGIN,
              payload: {"error":"Error Connection"}
            });
        });
  }
}
