import React from "react";
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import Header from "./Header";
import { CustomerListComponent } from "../pages/CustomerList";
import HomeComponent from "../pages/Home";
import { SideBarComponent } from "./Sidebar";
import AddCustomerComponent from "./../pages/Addcustomer";
import LoginComponent from '../pages/login';

const pathList = [

  {
    path:'/dashboard',
    component: HomeComponent
  },
  {
    path: '/dashboard/add-customer',
    component: AddCustomerComponent
  },
  {
    path: '/dashboard/customer-list',
    component: CustomerListComponent
  },
  {
    path: '/login',
    component: LoginComponent
  }
]


export const PrivateRoute = ({ component: Component, ...rest }) =>(
  <Route {...rest} render={props => (
      localStorage.getItem('AccessToken')
          ? <Component {...props} />
          : <Redirect to={{ pathname: '/login'}} />
  )} />
);


const Layout = () => {
    let token = null;
    token = localStorage.getItem('AccessToken');

    return (
      <div className="AppPanel">
          <Router>
            { token && <SideBarComponent></SideBarComponent> }
            { token && <Header></Header> }

            {
              pathList.map(data => {
                return (
                  <Route path={data.path} component={data.component} />
                )
              })
            }
        </Router>
      </div>
    );

}

export default Layout;
